mapper.update:
UPDATE parent SET experience_score = #{param1} 
WHERE 
parent_idx = 
		(SELECT parent_idx FROM children WHERE child_idx = #{param2});

mapper.select:
SELECT *
FROM playRecord
WHERE date_format(record_date, '%Y-%m-%d')  = curdate();

SELECT child_idx 
FROM children 
WHERE parent_idx = #{parentIdx}

SELECT s.song_idx, s.play_mode, s.title, s.thumbnail_url, s.video_url, s.preview_music_url, m.motion_tag, s.difficulty, s.calorie_consumption FROM songList s RIGHT JOIN (SELECT motion_type_idx, motion_tag FROM (SELECT * FROM motionType WHERE motion_type_idx BETWEEN 5 AND 9) AS t WHERE motion_tag LIKE
CONCAT('%', #{arr}, '%')
) AS m ON s.motion_type_idx = m.motion_type_idx ORDER BY RAND() LIMIT 1
